    add_filter( 'style_loader_tag',  'remove_https_http', 10, 4 );
    function remove_https_http( $html, $handle, $media,$tag ){
        $handles = array(
        	'dashicons',
        	'admin-bar',
        	'yoast-seo-adminbar',
        	'jetpack_css');
            if (is_front_page()) {
            if( in_array( $handle, $handles ) ){
            $html = str_replace("media='all'", "media='none' onload=\"if(media!='all')media='all'\"", $html);   
            }
            return $html;
        }
        else {
            return $html;
        }
    }
